INSERT INTO `nature` (`id`, `name`, `detail`) VALUES
(1, 'เป็นมิตร', 'ปลานิสัยดีรักสงบ ปลากลุ่มนี้ชอบอยู่รวมกันเป็นฝูง รักสงบไม่ทำร้ายกันเอง ไม่ชอบอยู่ตัวคนเดียวโดดๆเพราะกลัวเหงา(ฮี่ๆ)หากอยู่ตัวเดียวจะชิงเหงาตายจึงควรเลี้ยงเป็นฝูงเพื่อไม่ให้เหงาจนตาย สำหรับปลากลุ่มนี้หากจะเลี้ยงรวมกับปลาชนิดอื่นควรหาปลาที่มีขนาดเท่าๆกัน'),
(2, 'นักสู้', 'ปลานิสัยหวงถิ่น เป็นปลากลุ่มประเภทที่ถิ่นข้าใครอย่าแหยม เป็นปลาที่ชอบปักหลักเป็นที่เป็นทาง มักมีอณาเขตเป็นของตัวเอง ปลากล่มนี้ไม่ชอบอยู่ร่วมกับปลาชนิดอื่น นิสัยโดยส่วนตัวไม่ดุร้ายนะแต่หากใครข้ามถิ่นเข้ามาก็มีเรื่อง'),
(3, 'ก้าวร้าว', 'ปลานิสัยก้าวร้าว ปลาพวกนี้ชอบอยู่รวมกันเป็นฝูงแล้วไปยกพวกตีกับคนอื่นโฮ่ๆๆ ... (ปลาช่วงกลหรือไงฟะ) แต่พอวันไหนว่างๆไม่รู้จะไปหาเรื่องกับใครก็จะหันมาอัดกันเองคลายเครียด ดังนั้นปลากลุ่มนี้หากจะเลี้ยงรวมกับปลาอื่นควรจะหากปลาที่ตัวใหญ่กว่ามาเลี้ยง'),
(4, 'ดุร้าย', 'ปลานิสัยดุร้าย พวกปลากลุ่มนี้เป็นนักเลงตัวจริงครับ และมักจะเป็นปลากินเนื้อ ไม่ชอบอยู่ร่วมกับใครหน้าไหน ชอบเป็นพระเอกฉายเดี่ยววันแมนโชว์ ปลาพวกนี้นอกจะหาเรื่องอัดชาวบ้านแล้วแม้นแต่พวกเดียวกันมันก็ไม่เว้น ดังนั้นปลาพวกนี้จึงมักเป็นนักโทษขังเดี่ยวเลี้ยงตัวเดียว แต่หากจะเลี้ยงรวมกับปลาชนิดอื่น ที่มีความสามารถหลบหลีกว่องไว และตู้ที่เลี้ยงควรมีขนาดที่ใหญ่เพื่อมีพื้นที่ในการหลบหนี อีกทั้งการให้อาหารก็ควรให้อย่างพอเพียงเพื่อป้องกันการแย่งอาหารกัน'),
(5, 'หวงถิ่นอาศัย', 'เป็นปลากลุ่มประเภทที่ถิ่นข้าใครอย่าแหยม เป็นปลาที่ชอบปักหลักเป็นที่เป็นทาง มักมีอณาเขตเป็นของตัวเอง ปลากล่มนี้ไม่ชอบอยู่ร่วมกับปลาชนิดอื่น นิสัยโดยส่วนตัวไม่ดุร้ายนะแต่หากใครข้ามถิ่นเข้ามาก็มีเรื่อง');

